import React, { useState, useEffect } from "react";
import { useParams, useNavigate } from "react-router-dom";
import axios from "axios";

const StudentExamPage = () => {
  const { examId } = useParams();
  const navigate = useNavigate();
  const [exam, setExam] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [answers, setAnswers] = useState({});

  const studentEmail = sessionStorage.getItem('studentEmail');
  const studentStudentId = sessionStorage.getItem('studentId');

  useEffect(() => {
    // If student not logged in, redirect to login with examId in query param
    if (!studentEmail || !studentStudentId) {
      navigate(`/student/login?examId=${examId}`);
      return;
    }

    const fetchExam = async () => {
      try {
        const response = await axios.get(`http://localhost:5001/api/exams/${examId}`);
        if (response.data.success) {
          setExam(response.data.exam);
        } else {
          setError('Exam not found.');
        }
      } catch (err) {
        console.error("Error fetching exam:", err);
        setError('Failed to load exam.');
      } finally {
        setLoading(false);
      }
    };

    fetchExam();
  }, [examId, navigate, studentEmail, studentStudentId]);

  const handleAnswerChange = (questionId, value) => {
    setAnswers((prev) => ({ ...prev, [questionId]: value }));
  };

  const submitExam = async () => {
    if (!exam) return;

    const submissionAnswers = exam.questions.map((q) => ({
      questionId: q.id,
      answer: answers[q.id] || ""
    }));

    try {
      const response = await axios.post("http://localhost:5001/api/submissions", {
        email: studentEmail,
        examId: exam._id,
        answers: submissionAnswers
      });
      if (response.data.success) {
        alert("Exam submitted successfully.");
        // Redirect to a confirmation or student dashboard
        navigate('/student/exams'); // Make sure you have a student exams page or handle accordingly
      } else {
        alert("Failed to submit exam.");
      }
    } catch (err) {
      console.error("Error submitting exam:", err);
      alert("Error submitting exam.");
    }
  };

  if (loading) return <div className="p-4">Loading exam...</div>;
  if (error) return <div className="p-4 text-red-500">{error}</div>;
  if (!exam) return null;

  return (
    <div className="p-6 bg-white min-h-screen">
      <h1 className="text-2xl font-bold mb-4">Exam</h1>
      <p><strong>Deadline:</strong> {new Date(exam.deadline).toLocaleString()}</p>
      <p><strong>Duration:</strong> {exam.duration} minutes</p>
      <div className="mt-4 space-y-4">
        {exam.questions.map((q) => (
          <div key={q.id} className="bg-gray-50 p-4 rounded shadow">
            <h3 className="font-semibold">{q.question}</h3>
            <textarea
              className="w-full border rounded p-2 mt-2"
              rows="4"
              placeholder="Type your answer here..."
              value={answers[q.id] || ""}
              onChange={(e) => handleAnswerChange(q.id, e.target.value)}
            ></textarea>
          </div>
        ))}
      </div>
      <button
        onClick={submitExam}
        className="mt-4 bg-yellow-500 text-white px-4 py-2 rounded hover:bg-yellow-600"
      >
        Submit Exam
      </button>
    </div>
  );
};

export default StudentExamPage;
