import React, { useState } from "react";
import {
  ArrowRightIcon,
  ArrowDownTrayIcon, // CSV Export Icon
} from "@heroicons/react/24/outline";
import { Line } from "react-chartjs-2";
import { Chart as ChartJS, LineElement, PointElement, Tooltip } from "chart.js";

// Register Chart.js elements
ChartJS.register(LineElement, PointElement, Tooltip);

const Performance = () => {
  // Dynamic data
  const students = [
    {
      id: "123456",
      name: "John Doe",
      email: "john@example.com",
      performance: "Gold",
      scores: [80, 85, 90, 92],
      pastResults: [
        {
          date: "2023-12-01",
          grammar: 4,
          punctuation: 2,
          spelling: 1,
          score: 90,
          feedback: "Improved grammar usage, minor punctuation issues.",
        },
        {
          date: "2023-11-24",
          grammar: 8,
          punctuation: 5,
          spelling: 3,
          score: 75,
          feedback: "Significant grammar and punctuation errors.",
        },
      ],
    },
    {
      id: "234567",
      name: "Jane Smith",
      email: "jane@example.com",
      performance: "Silver",
      scores: [70, 75, 80, 85],
      pastResults: [
        {
          date: "2023-12-01",
          grammar: 2,
          punctuation: 1,
          spelling: 0,
          score: 95,
          feedback: "Excellent overall performance.",
        },
        {
          date: "2023-11-24",
          grammar: 5,
          punctuation: 3,
          spelling: 2,
          score: 80,
          feedback: "Some grammar and punctuation improvements needed.",
        },
      ],
    },
  ];

  const [searchQuery, setSearchQuery] = useState("");
  const [expandedStudent, setExpandedStudent] = useState(null);
  const [selectedPastResult, setSelectedPastResult] = useState(null);

  const filteredStudents = students.filter(
    (student) =>
      student.name.toLowerCase().includes(searchQuery.toLowerCase()) ||
      student.id.includes(searchQuery)
  );

  const handleExportCSV = () => {
    // Logic for exporting to CSV
    alert("CSV Exported!");
  };

  const chartOptions = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      tooltip: {
        callbacks: {
          label: (tooltipItem) => `Score: ${tooltipItem.raw}%`,
        },
      },
      legend: { display: false },
    },
    scales: {
      x: {
        grid: { display: false },
        ticks: { display: false },
      },
      y: {
        grid: { display: false },
        ticks: { display: false },
      },
    },
  };

  return (
    <div className="p-6 bg-gray-100 min-h-screen">
      {/* Header Section */}
      <header className="flex justify-between items-center mb-6">
        <h1 className="text-3xl font-semibold text-gray-800">
          Student Performance
        </h1>
        <div className="flex space-x-4">
          <input
            type="text"
            placeholder="Search students or IDs..."
            className="border rounded-md p-2"
            value={searchQuery}
            onChange={(e) => setSearchQuery(e.target.value)}
          />
          <button
            onClick={handleExportCSV}
            className="bg-yellow-500 text-white px-4 py-2 rounded-md flex items-center space-x-2"
          >
            <ArrowDownTrayIcon className="h-5 w-5" />
            <span>Export to CSV</span>
          </button>
        </div>
      </header>

      {/* Student List */}
      <div className="space-y-4">
        {filteredStudents.map((student) => (
          <div
            key={student.id}
            className={`bg-white shadow-md rounded-lg p-4 transition-all ${
              expandedStudent === student.id ? "h-auto" : "h-24"
            }`}
          >
            <div className="flex justify-between items-center">
              <div>
                <h3 className="text-lg font-semibold text-gray-800">
                  {student.name} - ID: {student.id}
                </h3>
                <p className="text-gray-600">{student.email}</p>
              </div>
              <div className="flex items-center space-x-4">
                <span
                  className={`px-2 py-1 rounded-full ${
                    student.performance === "Gold"
                      ? "bg-yellow-100 text-yellow-600"
                      : student.performance === "Silver"
                      ? "bg-gray-200 text-gray-600"
                      : "bg-orange-100 text-orange-600"
                  }`}
                >
                  {student.performance}
                </span>
                <button
                  className="text-blue-600 font-semibold"
                  onClick={() =>
                    setExpandedStudent(
                      expandedStudent === student.id ? null : student.id
                    )
                  }
                >
                  {expandedStudent === student.id ? "Collapse" : "Expand"}
                </button>
              </div>
            </div>
            {/* Expanded Details */}
            {expandedStudent === student.id && (
              <div className="mt-4">
                <div className="mb-4" style={{ height: "150px" }}>
                  <Line
                    data={{
                      labels: ["Week 1", "Week 2", "Week 3", "Week 4"],
                      datasets: [
                        {
                          label: "Scores",
                          data: student.scores,
                          borderColor: "#F59E0B",
                          borderWidth: 2,
                          pointRadius: 4,
                          pointBackgroundColor: "#F59E0B",
                          tension: 0.3,
                        },
                      ],
                    }}
                    options={chartOptions}
                  />
                </div>
                <h4 className="text-md font-semibold text-gray-800 mb-2">
                  Past Results
                </h4>
                <ul className="space-y-2">
                  {student.pastResults.map((result, index) => (
                    <li
                      key={index}
                      className="cursor-pointer border p-2 rounded-md hover:bg-gray-100"
                      onClick={() =>
                        setSelectedPastResult(
                          selectedPastResult === result.date ? null : result.date
                        )
                      }
                    >
                      {result.date} - Score: {result.score}%
                      {selectedPastResult === result.date && (
                        <div className="mt-2 text-gray-600">
                          <p>Grammar: {result.grammar} mistakes</p>
                          <p>Punctuation: {result.punctuation} mistakes</p>
                          <p>Spelling: {result.spelling} mistakes</p>
                          <p>Feedback: {result.feedback}</p>
                        </div>
                      )}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};

export default Performance;
