// src/pages/LoginPage.jsx

import React, { useState } from "react";
import { useNavigate } from "react-router-dom";
import axios from "axios"; // Ensure axios is installed

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [errorMessage, setErrorMessage] = useState("");
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    try {
      console.log("Login Attempt:", { email, password });

      const response = await axios.post("http://localhost:5001/api/login", {
        email,
        password,
      });

      console.log("Login Response:", response.data);

      if (response.data.success) {
        // Store the user's role if needed
        localStorage.setItem("role", response.data.role);

        // Navigate based on role
        if (response.data.role === "teacher") {
          navigate("/teacher");
        } else if (response.data.role === "student") {
          // Typically, students access via unique links, so you might want to show a message
          alert("Students should access exams via unique links.");
        }
      } else {
        setErrorMessage("Invalid credentials. Please try again.");
      }
    } catch (error) {
      console.error("Error logging in:", error);
      setErrorMessage(
        error.response?.data?.message || "Login failed. Please try again."
      );
    }
  };

  return (
    <div className="min-h-screen flex justify-center items-center bg-gray-100">
      <form
        className="bg-white p-6 rounded shadow-md w-full max-w-sm"
        onSubmit={handleLogin}
      >
        <h1 className="text-2xl font-semibold mb-4">Teacher Login</h1>
        {errorMessage && (
          <div className="bg-red-100 text-red-600 p-2 mb-4 rounded">
            {errorMessage}
          </div>
        )}
        <div className="mb-4">
          <label
            htmlFor="email"
            className="block text-sm font-medium text-gray-700"
          >
            Email
          </label>
          <input
            id="email"
            type="email"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            placeholder="Enter your email"
            required
          />
        </div>
        <div className="mb-6">
          <label
            htmlFor="password"
            className="block text-sm font-medium text-gray-700"
          >
            Password
          </label>
          <input
            id="password"
            type="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            className="w-full border rounded p-2 mt-1"
            placeholder="Enter your password"
            required
          />
        </div>
        <button
          type="submit"
          className="w-full bg-yellow-500 text-white py-2 rounded font-semibold hover:bg-yellow-600"
        >
          Login
        </button>
      </form>
    </div>
  );
};

export default LoginPage;
